import React, {useEffect, useState} from 'react';
import { useFrontContext } from '../providers/frontContext';
import {PluginLayout, Button, Input} from '@frontapp/ui-kit';
import data from '@emoji-mart/data'
import Picker from '@emoji-mart/react'
const emoji = require("emoji-dictionary");
/* Things to fix:

      -Add Emoji picker support 
      -Give option to set duration for Slack Status

*/

function Tutorial() {

  const context = useFrontContext();
  const [color1, setColor1] = useState('green');
  const [color2, setColor2] = useState('green');
  const [disabled1, setDisabled1] = useState(false);
  const [disabled2, setDisabled2] = useState(false);
  const [slackStatus,setSlackStatus] = useState('');
  const [emojiText,setEmojiText] = useState('');
  const [frontStatusCheck, setFrontStatusCheck] = useState(false);
  const [currentSlackStatus,setCurrentSlackStatus] = useState('');
  const [currentEmoji,setCurrentEmoji] = useState('');
  const [isPickerOpen, setIsPickerOpen] = useState(false);
  const frontuserID = context.teammate.id;
  const webHookURL = "http://localhost:3000" 

  const [selectedEmoji, setSelectedEmoji] = useState('');

  useEffect(() => {
    const timeout1 = setTimeout(() => {
      setDisabled1(false);
      setColor1('green');
    }, 2000);
    const timeout2 = setTimeout(() => {
      setDisabled2(false);
      setColor2('green');
    }, 2000);
    return () => {
      clearTimeout(timeout1);
      clearTimeout(timeout2);
    }
  }, [disabled1, disabled2]);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(webHookURL + '/current');
        const data = await response.json();
        setCurrentSlackStatus(data.status_text);
        setCurrentEmoji(data.status_emoji);
      } catch (error) {
        console.log(error);
      }
    };
  
    // Fetch data initially and then fetch it every 3 seconds
    fetchData();
    const interval = setInterval(() => {
      fetchData();
    }, 3000);
  
    // Clean up the interval to avoid memory leaks
    return () => clearInterval(interval);
  }, []);

  const handleEmojiSelect = (emoji) => {
    setSelectedEmoji(emoji.shortcodes);
    //send emoji.colons to API POST endpoint here
  }

  const handlePickerToggle = () => {
    setIsPickerOpen(!isPickerOpen);
  };

  const handleClick = async () => {
      if(disabled1){
        return;
      }
        //const slackPayload = {slack_status:{slackStatusText}};
        setColor1(disabled1 ? 'green' : 'red');
        setDisabled1(true); 
        
          const response = await fetch(webHookURL+'/slackStatus', {
            method: 'POST',
            body: JSON.stringify({slack_status: slackStatus, emoji: emojiText}), //fix this!!!
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response.ok) {
            console.error('Failed to post data to webhook.');
          }
          
          const response2 = await fetch(webHookURL+'/frontStatus', {
            method: 'POST',
            body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response2.ok) {
            console.error('Failed to post data to webhook.');
          }
          setSlackStatus("");
          setEmojiText("");
          handleCheckboxChange(false);
  };

  const handleStatusChange = async (slackStatus) => {
    setSlackStatus(slackStatus);
  };

  const handleStatusChange2 = async (emojiText) => {
    setEmojiText(emojiText);
  };

  const handleCheckboxChange = (checked) => {
    setFrontStatusCheck(checked);
  };


  const clearClick = async () => {
    if(disabled2){
      return;
    }
      //const slackPayload = {slack_status:{slackStatusText}};
        setColor2(disabled2 ? 'green' : 'red');
        setDisabled2(true); 
        //inputRef.current.value = '';
        const response = await fetch(webHookURL+'/slackStatus', {
          method: 'POST',
          body: JSON.stringify({slack_status: "", emoji: ""}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          console.error('Failed to post data to webhook.');
        }
        
        const response2 = await fetch(webHookURL+'/frontStatus', {
          method: 'POST',
          body: JSON.stringify({id: frontuserID, front_status: true}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response2.ok) {
          console.error('Failed to post data to webhook.');
        }
        
        
};


const uniCodeEmoji = emoji.getUnicode(currentEmoji)
const uniCodeSelectedEmoji = emoji.getUnicode(selectedEmoji)
return (
  <PluginLayout>
  <h1 style={{ textAlign: 'center', fontSize: '20px', padding: '10px'}}>Current slack status:</h1>
  <div style={{ display: 'flex', justifyContent: 'center',border: '3px solid black', borderRadius: '5px', minWidth: 'fit-content', width: '150px',margin:'0 auto' }}> 
  <h1 style={{
    textAlign: uniCodeEmoji ? 'left' : 'center',
    fontSize: '20px',
    padding: '10px',
    margin: '0'
  }}>
    {currentSlackStatus ? currentSlackStatus : 'No status is set in Slack...'}
  </h1>
  <h1 style={{ textAlign: 'center', fontSize: '20px', padding: '10px', margin: '0' }}>
    {uniCodeEmoji}
  </h1>
</div>
  <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '250px' }}>
  <div style={{ padding: '10px' }}>
    <label htmlFor="input" style={{ marginBottom: '10px' }}>
      Enter Slack Status Text:
    </label>
    
    <Input
      type="text"
      maxWidth="200"
      value={slackStatus}
      onChange={handleStatusChange}
      placeholder="Enter Slack status here"
      style={{ marginBottom: '10px' }}
    />
    </div>

    <div style={{ padding: '10px' }}>
    <label htmlFor="input" style={{ marginBottom: '10px' }}>
      Enter Slack emoji Text:
    </label>
    <Input
      type="text"
      maxWidth="200"
      value={emojiText}
      onChange={handleStatusChange2}
      placeholder="Enter Emoji text here"
      style={{ marginBottom: '10px' }}
    />
    </div>
    <div style={{ padding: '10px' }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
      <label htmlFor="checkbox" style={{ marginRight: '10px' }}>
        Set OOO in Front?
      </label>
      <input
        type="checkbox"
        id="checkbox"
        checked={frontStatusCheck}
        onChange={(event) => handleCheckboxChange(event.target.checked)}
      />
      </div>
    </div>
    <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
      <Button
        style={{ flex: '1', marginRight: '10px', backgroundColor: color1 }}
        onClick={handleClick}
        disabled={disabled1}
      >
        {disabled1 ? 'Updating Status...' : 'Change Status'}
      </Button>
      <Button
        style={{ flex: '1', backgroundColor: color2 }}
        onClick={clearClick}
        disabled={disabled2}
      >
        {disabled2 ? 'Clearing Status...' : 'Clear Status'}
      </Button>
    </div>
  </div>
  <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
  <div style={{ textAlign: 'center' }}>
    <button onClick={handlePickerToggle}>Select Emoji</button>
    {isPickerOpen && (
      <Picker data={data} onSelect={handleEmojiSelect} />
    )}
    <h1>{uniCodeSelectedEmoji}</h1>
  </div>
</div>

</PluginLayout>
);
}
export default Tutorial;
