import React, {useEffect, useState, useRef} from 'react';
import { useFrontContext } from '../providers/frontContext';
import {PluginLayout, Button, Input} from '@frontapp/ui-kit';

/* Things to fix:

      -Add Emoji picker support 
      -Show slack status in Front
      -Fix logic with clear status button
      -Give option to set duration for Slack Status

*/


function Tutorial() {

  const context = useFrontContext();
  const [color1, setColor1] = useState('green');
  const [color2, setColor2] = useState('green');
  const [disabled1, setDisabled1] = useState(false);
  const [disabled2, setDisabled2] = useState(false);
  const [slackStatus,setSlackStatus] = useState('');
  const [emojiText,setEmojiText] = useState('');
  const [frontStatusCheck, setFrontStatusCheck] = useState(false);
  const frontuserID = context.teammate.id;
  const webHookURL = "https://fa8c-68-36-121-182.ngrok.io" 
  const inputRef = useRef(null);
  const [currentSlackStatus, setCurrentSlackStatus] = useState({});

  useEffect(() => {
    fetch('/slack-status')
      .then(response => response.json())
      .then(data => setCurrentSlackStatus(data))
      .catch(error => console.error(error));
  }, []);
  
  useEffect(() => {
    const timeout1 = setTimeout(() => {
      setDisabled1(false);
      setColor1('green');
    }, 2000);
    const timeout2 = setTimeout(() => {
      setDisabled2(false);
      setColor2('green');
    }, 2000);
  
    return () => {
      clearTimeout(timeout1);
      clearTimeout(timeout2);
    }
  }, [disabled1, disabled2]);

  const handleClick = async () => {
      if(disabled1){
        return;
      }
        //const slackPayload = {slack_status:{slackStatusText}};
        setColor1(disabled1 ? 'green' : 'red');
        setDisabled1(true); 
        
          const response = await fetch(webHookURL+'/slackStatus', {
            method: 'POST',
            body: JSON.stringify({slack_status: slackStatus, emoji: emojiText}), //fix this!!!
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response.ok) {
            console.error('Failed to post data to webhook.');
          }
          
          const response2 = await fetch(webHookURL+'/frontStatus', {
            method: 'POST',
            body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response2.ok) {
            console.error('Failed to post data to webhook.');
          }
          
  };

  const handleStatusChange = async (slackStatus) => {
    setSlackStatus(slackStatus);
  };

  const handleStatusChange2 = async (emojiText) => {
    setEmojiText(emojiText);
  };

  const handleCheckboxChange = event => {
    setFrontStatusCheck(event.target.checked);
  };


  const clearClick = async () => {
    if(disabled2){
      return;
    }
      //const slackPayload = {slack_status:{slackStatusText}};
        setColor2(disabled2 ? 'green' : 'red');
        setDisabled2(true); 
        inputRef.current.value = '';
        const response = await fetch(webHookURL+'/slackStatus', {
          method: 'POST',
          body: JSON.stringify({slack_status: "", emoji: ""}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          console.error('Failed to post data to webhook.');
        }
        
        const response2 = await fetch(webHookURL+'/frontStatus', {
          method: 'POST',
          body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response2.ok) {
          console.error('Failed to post data to webhook.');
        }

};
return (
  <PluginLayout>
  <h1 style={{ textAlign: 'center', fontSize: '20px', padding: '10px'}}>Current slack status:</h1>
  <div>
      <h1>{currentSlackStatus.status_text}</h1>
      <img src={currentSlackStatus.status_emoji} alt="Slack status emoji" />
    </div>
  <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '250px' }}>
  <div style={{ padding: '10px' }}>
    <label htmlFor="input" style={{ marginBottom: '10px' }}>
      Enter Slack Status Text:
    </label>
    
    <Input
      type="text"
      maxWidth="200"
      value={slackStatus}
      onChange={handleStatusChange}
      placeholder="Enter Slack status here"
      ref={inputRef}
      style={{ marginBottom: '10px' }}
    />
    </div>

    <div style={{ padding: '10px' }}>
    <label htmlFor="input" style={{ marginBottom: '10px' }}>
      Enter Slack emoji Text:
    </label>
    <Input
      type="text"
      maxWidth="200"
      value={emojiText}
      onChange={handleStatusChange2}
      placeholder="Enter Emoji text here"
      ref={inputRef}
      style={{ marginBottom: '10px' }}
    />
    </div>
    <div style={{ padding: '10px' }}>
    <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
      <label htmlFor="checkbox" style={{ marginRight: '10px' }}>
        Set OOO in Front?
      </label>
      <input
        type="checkbox"
        id="checkbox"
        checked={frontStatusCheck}
        onChange={handleCheckboxChange}
      />
      </div>
    </div>
    <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
      <Button
        style={{ flex: '1', marginRight: '10px', backgroundColor: color1 }}
        onClick={handleClick}
        disabled={disabled1}
      >
        {disabled1 ? 'Updating Status...' : 'Send Status'}
      </Button>
      <Button
        style={{ flex: '1', backgroundColor: color2 }}
        onClick={clearClick}
        disabled={disabled2}
      >
        {disabled2 ? 'Clearing Status...' : 'Clear Status'}
      </Button>
    </div>
  </div>
</PluginLayout>
);
}
export default Tutorial;
