import React, {useEffect, useState} from 'react';

import { useFrontContext } from '../providers/frontContext';

function Tutorial() {

  const context = useFrontContext();
  const [color1, setColor1] = useState('red');
  const [color2, setColor2] = useState('gree')
  const [disabled, setDisabled] = useState(false);
  const [emojiCode,setEmojiCode] = useState('');
  const [slackStatus,setSlackStatus] = useState('');
  const [frontStatusCheck, setFrontStatusCheck] = useState(false);
  const frontuserID = context.teammate.id;

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setColor1('red');
      setDisabled(false); 
    }, 4000);

    return () => {
      clearTimeout(timeoutId);
    };
  }, [color1,disabled]);


  const handleClick = async () => {
      if(disabled){
        return;
      }
        //const slackPayload = {slack_status:{slackStatusText}};
          setColor1('green');
          setDisabled(true); 

          const response = await fetch(' https://a387-68-36-121-182.ngrok.io/slackStatus', {
            method: 'POST',
            body: JSON.stringify({slack_status: slackStatus, emoji: emojiCode}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response.ok) {
            console.error('Failed to post data to webhook.');
          }
          
          const response2 = await fetch(' https://a387-68-36-121-182.ngrok.io/frontStatus', {
            method: 'POST',
            body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response2.ok) {
            console.error('Failed to post data to webhook.');
          }

  };

  const handleEmojiChange = event => {
    setEmojiCode(event.target.value);
  };

  const handleStatusChange = event => {
    setSlackStatus(event.target.value);
  };

  const handleCheckboxChange = event => {
    setFrontStatusCheck(event.target.checked);
  };


  const clearClick = async () => {
    if(disabled){
      return;
    }
      //const slackPayload = {slack_status:{slackStatusText}};
        setColor2('green');
        setDisabled(true); 

        const response = await fetch(' https://a387-68-36-121-182.ngrok.io/slackStatus', {
          method: 'POST',
          body: JSON.stringify({slack_status: "", emoji: ""}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          console.error('Failed to post data to webhook.');
        }
        
        const response2 = await fetch(' https://a387-68-36-121-182.ngrok.io/frontStatus', {
          method: 'POST',
          body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response2.ok) {
          console.error('Failed to post data to webhook.');
        }

};



  return (
      <div class = "button-container">
        <div class = "textinput">
          <label htmlFor="input">Enter Slack Status Text:</label>
          <input type="text" value={slackStatus} onChange={handleStatusChange} placeholder="Enter Slack status here"/>
          <label htmlFor="input">Enter Emoji Code:</label>
          <input type="text" value={emojiCode} onChange={handleEmojiChange} placeholder="Enter emoji code here" />
          <label>
          <input type="checkbox" checked={frontStatusCheck} onChange={handleCheckboxChange} /> Set yourself OOO in Front?
          </label>
          <button style={{ backgroundColor: color1 }} onClick={handleClick} disabled={disabled}>
            {disabled ? 'Updating Status...' : 'Send Status'}
          </button>
          <br></br>
          <button style={{ backgroundColor: color2 }} onClick={clearClick} disabled={disabled}>
            {disabled ? 'Clearing Status...' : 'Clear Status'}
          </button>
        </div>
    </div>
  );
}

export default Tutorial;
