import React, { useState } from 'react';

const EmojiSelector = () => {
  // Define the list of emojis
  const emojis = ['ðŸ˜€', 'ðŸ˜„', 'ðŸ˜', 'ðŸš€', 'ðŸŽ‰'];

  // State to keep track of the selected emoji
  const [selectedEmoji, setSelectedEmoji] = useState(null);

  // Event handler for emoji selection
  const handleEmojiSelection = (emoji) => {
    setSelectedEmoji(emoji);
  };

  return (
    <div>
      <h1>Emoji Selector</h1>
      <div>
        {emojis.map((emoji, index) => (
          <button
            key={index}
            onClick={() => handleEmojiSelection(emoji)}
            style={{ fontSize: '24px', margin: '5px' }}
          >
            {emoji}
          </button>
        ))}
      </div>
      <div>
        {selectedEmoji && (
          <h2>
            You have selected: <span style={{ fontSize: '48px' }}>{selectedEmoji}</span>
          </h2>
        )}
      </div>
    </div>
  );
};

export default EmojiSelector;
