import React, {useEffect, useState} from 'react';

import { useFrontContext } from '../providers/frontContext';

function Tutorial() {

  const context = useFrontContext();
  const [color, setColor] = useState('red');
  const [disabled, setDisabled] = useState(false);
  const frontuserID = context.teammate.id;
  
  const [emojiCode,setEmojiCode] = useState('');
  const [slackStatus,setSlackStatus] = useState('');


  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setColor('red');
      setDisabled(false); 
    }, 4000);

    return () => {
      clearTimeout(timeoutId);
    };
  }, [color,disabled]);


  const handleClick = async () => {
      if(disabled){
        return;
      }
        //const slackPayload = {slack_status:{slackStatusText}};
          setColor('green');
          setDisabled(true); 

          const response = await fetch('https://079f-68-36-121-182.ngrok.io/slackStatus', {
            method: 'POST',
            body: JSON.stringify({slack_status: slackStatus, emoji: emojiCode}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response.ok) {
            console.error('Failed to post data to webhook.');
          }

  };

  const handleEmojiChange = event => {
    setEmojiCode(event.target.value);
  };

  const handleStatusChange = event => {
    setSlackStatus(event.target.value);
  };

  return (
      <div class = "button-container">
        <div class = "textinput">
          <label htmlFor="input">Enter Status Text:</label>
          <input type="text" value={slackStatus} onChange={handleStatusChange} placeholder="Enter Slack status here"/>
          <label htmlFor="input">Enter Emoji Code:</label>
          <input type="text" value={emojiCode} onChange={handleEmojiChange} placeholder="Enter emoji code here" />
          <button style={{ backgroundColor: color }} onClick={handleClick} disabled={disabled}>
            {disabled ? 'Updating Status...' : 'Send Status'}
          </button>
        </div>
    </div>
  );
}

export default Tutorial;
