import React, {useEffect, useState, useRef} from 'react';
import Picker from 'react-emoji-picker';
import 'react-emoji-picker/dist/index.css';
import EmojiWheel from 'react-emoji-wheel';
import { useFrontContext } from '../providers/frontContext';

function Tutorial() {

  const context = useFrontContext();
  const [color1, setColor1] = useState('green');
  const [color2, setColor2] = useState('green');
  const [disabled1, setDisabled1] = useState(false);
  const [disabled2, setDisabled2] = useState(false);
  const [selectedEmoji, setSelectedEmoji] = useState('');
  const [slackStatus,setSlackStatus] = useState('');
  const [frontStatusCheck, setFrontStatusCheck] = useState(false);
  const frontuserID = context.teammate.id;
  const webHookURL = "https://fd3e-68-36-121-182.ngrok.io" 
  const inputRef = useRef(null);
  
  useEffect(() => {
    const timeout1 = setTimeout(() => {
      setDisabled1(false);
      setColor1('green');
    }, 4000);
    const timeout2 = setTimeout(() => {
      setDisabled2(false);
      setColor2('green');
    }, 4000);

    return () => {
      clearTimeout(timeout1);
      clearTimeout(timeout2);
    }
  }, [disabled1, disabled2]);
 
  const handleEmojiSelect = (emoji) => {
    setSelectedEmoji(emoji);
  };

  const handleClick = async () => {
      if(disabled1){
        return;
      }
        //const slackPayload = {slack_status:{slackStatusText}};
        setColor1(disabled1 ? 'green' : 'red');
        setDisabled1(true); 
        
          const response = await fetch(webHookURL+'/slackStatus', {
            method: 'POST',
            body: JSON.stringify({slack_status: slackStatus, emoji: emoji}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response.ok) {
            console.error('Failed to post data to webhook.');
          }
          
          const response2 = await fetch(webHookURL+'/frontStatus', {
            method: 'POST',
            body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          if (!response2.ok) {
            console.error('Failed to post data to webhook.');
          }
          
  };

  const handleStatusChange = event => {
    setSlackStatus(event.target.value);
  };

  const handleCheckboxChange = event => {
    setFrontStatusCheck(event.target.checked);
  };


  const clearClick = async () => {
    if(disabled2){
      return;
    }
      //const slackPayload = {slack_status:{slackStatusText}};
        setColor2(disabled2 ? 'green' : 'red');
        setDisabled2(true); 
        inputRef.current.value = '';
        const response = await fetch(webHookURL+'/slackStatus', {
          method: 'POST',
          body: JSON.stringify({slack_status: "", emoji: ""}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          console.error('Failed to post data to webhook.');
        }
        
        const response2 = await fetch(webHookURL+'/frontStatus', {
          method: 'POST',
          body: JSON.stringify({id: frontuserID, front_status: !frontStatusCheck}),
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (!response2.ok) {
          console.error('Failed to post data to webhook.');
        }

};
return (
  <div className="button-container">
    <div className="textinput">
      <label htmlFor="input">Enter Slack Status Text:</label>
      <input
        type="text"
        value={slackStatus}
        onChange={handleStatusChange}
        placeholder="Enter Slack status here"
        ref={inputRef}
      />
      <label>
          Selected Emoji: {selectedEmoji} {/* display selected emoji */}
        </label>
        <EmojiWheel onSelect={handleEmojiSelect} />
        <p>Selected emoji: {emoji}</p>
      <button
        style={{ backgroundColor: color1 }}
        onClick={handleClick}
        disabled={disabled1}
      >
        {disabled1 ? 'Updating Status...' : 'Send Status'}
      </button>
      <br />
      <button
        style={{ backgroundColor: color2 }}
        onClick={clearClick}
        disabled={disabled2}
      >
        {disabled2 ? 'Clearing Status...' : 'Clear Status'}
      </button>
    </div>
  </div>
);
}

export default Tutorial;
